// 1.引入React组件
import React ,{Component,Fragment} from 'react';


//2.用class定义组件
class ReactDemo extends Component{

    //组件默认加载数据
    constructor(props){
        super(props);
        //state 负责存储组件里的数据
        this.state={
            list:[
                '学习Flutter',
                '学习React',
                '学习Vue'
            ],
            inputValue:''
        }
    }


//3.render函数return 模板
    render(){
        return(
            <Fragment>
                <div>
                    {/* 绑定数据以及事件时必须在前面加this  bind绑定this指向*/}
                    <input value={this.state.inputValue} placeholder='TodoList'
                           onChange={this.handleInputValue.bind(this)}
                    ></input>
                    {/* this很重要 */}
                    <button onClick={this.handleBtn.bind(this)}>提交</button>
                    <ul>
                        {   //Es6map循环
                            this.state.list.map((item,index)=>{
                                return  <li key={index} onClick={this.handleDelete.bind(this,index)}>
                                            {item}
                                        </li>
                            })
                        }
                    </ul>
                </div>
            </Fragment>
        );
    }

    handleInputValue(e){
        this.setState({
            inputValue:e.target.value
        })
    }

    //数组新增
    handleBtn(){
        this.setState({
                //Es6数组连接 ...展开运算符
                //数组新增
            list:[...this.state.list,this.state.inputValue],
            inputValue:''

        })
    }
    //数组删除
    handleDelete(index){
        console.log(index)
    }

}

//4.输出
export default ReactDemo;
