// 1.引入React组件
import React ,{Component,Fragment} from 'react';


//2.用class定义组件
class ReactDemo extends Component{

    //组件默认加载数据
    constructor(props){
        super(props);
        //state 负责存储组件里的数据
        this.state={
            list:[],
            inputValue:'666'
        }
    }


//3.render函数return 模板
    render(){
        return(
            <Fragment>
                <div>
                    {/* 绑定数据以及事件时必须在前面加this */}
                    <input value={this.state.inputValue} placeholder='TodoList'
                           onChange={this.handleInputValue.bind(this)}
                    ></input>
                    <button onClick={handleBtn.bind(this)}>提交</button>
                    <ul>
                        {
                            this.state.list.map((item,index)=>{
                                return <li>{item}</li>
                            })
                        }
                    </ul>
                </div>
            </Fragment>
        );
    }

    handleInputValue(e){
        this.setState({
            inputValue:e.target.value
        })
    }
}

//4.输出
export default ReactDemo;
