import React ,{Component,Fragment} from 'react';
import DemoItem from './DemoItem';


class Demo extends Component{
    constructor(props){
        super(props);
        this.state={
            list:[
                
            ],
            value:'',
        }
        this.InputValue=this.InputValue.bind(this)
        this.PushListBtn=this.PushListBtn.bind(this)
        this.RmListItem=this.RmListItem.bind(this)
    }
    render(){
        console.log('prent render')
        return (
            <Fragment>
                <div>
                    输入内容：
                    <input onChange={this.InputValue} value={this.state.value}></input>
                    <button onClick={this.PushListBtn}>提交</button>
                </div>
                <ul>
                   {
                       this.MapItem()
                   }
                </ul>
            </Fragment>
        )
    }

    MapItem(){
       return this.state.list.map((item,index)=>{
            return  (
                <DemoItem
                ReListItem={this.RmListItem} 
                key={index}
                index={index}
                item={item}/>
            )
        })
    }

    InputValue(e){
        let value=e.target.value;
        this.setState(()=>{
            return{
                value
            }
        })
    }
    
    PushListBtn(){
        console.log(this.state.value)
        this.setState((prevState)=>{
            return {
                list:[...prevState.list,this.state.value],
                value:''
            }
        },()=>{
            console.log(this.state.list)
        }
        )
    }

    RmListItem(index){
      this.setState((prevState)=>{
          const list=prevState.list;
          list.splice(index,1)
        return{
            list
        }
      })
    }

}

export default Demo;
